import React, { useEffect } from "react";
import style from "./productDetail.module.css";
import { useParams } from "react-router-dom";
import { getOneProduct } from "../../../products";
import { useSelector, useDispatch } from "react-redux";

export const ProductDetail = () => {
  const dispatch = useDispatch();
  const { product } = useSelector((state) => state.product);
  const { id } = useParams();

  useEffect(() => {
    dispatch(getOneProduct(id));
    console.log(product, id);
  }, [dispatch]);

  return (
    <div className={style.product__container}>
      <h1>Detalle del Producto:</h1>
      <div className={style.product__details}>
      <img className={style.image} src={product.image} />


      <span className={style.card__price}>
        <span className={style.card__title}>Precio:</span> ${product.price}
      </span>
      <span className={style.card__description}>
        <span className={style.card__title}> Descripci√≥n:</span>{" "}
        {product.description}
      </span>
      <span className={style.card__quantity}>
        <span className={style.card__title}> Disponibles: </span>{" "}
        {product.stock}
      </span>
      </div>

    </div>
  );
};
