export const getProducts = createAsyncThunk(
  "productsList/getProducts",
  async ({page1,limit=1,keyword=''},{dispatch,getState}) => {
const {}=getState
    //    return fetch('http://localhost:3000/products').then(res=> res.json())
    const res = await fetch(`http://localhost:3000/products?page=${page}&limit=${limit}&keyword=${keyword}`);
