import { createAsyncThunk } from "@reduxjs/toolkit";

export const getProducts = createAsyncThunk(
  "productsList/getProducts",
//   async ({page,limit,keyword},{dispatch,getState}) => {
  async ({page,limit,keyword},{dispatch,getState}) => {
    console.log('datos')
   let dataPage= page ||'1'
   let dataLimit= limit ||'10'
  let dataKeyword =keyword || ''
// const {}=getState
    //    return fetch('http://localhost:3000/products').then(res=> res.json())
    const res = await fetch(`http://localhost:3000/products?page=${dataPage}&limit=${dataLimit}&keyword=${dataKeyword}`);
    const data = await res.json();
    return data;
  }
);
