import React, { useEffect, useState, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import style from "./productList.module.css";
import { getProducts } from "../../../products";
export const ProductList = () => {
  const inputSearch = useRef(null);
  const { productsList } = useSelector((state) => state.productsList);
  const dispatch = useDispatch();
  const [products, setProducts] = useState({});
  const [filters, setFilters] = useState({
    page: "1",
    limit: undefined,
    keyword: "",
  });
  useEffect(() => {
    dispatch(getProducts(filters));
  }, [dispatch]);

  const onChangeTextSearch = (e) => {
    e.preventDefault();
    const text = inputSearch.current.value;
    setFilters({ page: "1", limit: undefined, keyword: text });
  };

  const onSearchSubmit = (e) => {
    if (e.key !== "Enter") return;
    dispatch(getProducts(filters));
    inputSearch.current.value = "";
    setFilters({ page: "1", limit: undefined, keyword: "" });
  };
  const onClickSearchSubmit = (e) => {
    e.preventDefault();
    dispatch(getProducts(filters));
    inputSearch.current.value = "";
    setFilters({ page: "1", limit: undefined, keyword: "" });
  };

  return (
    <div className={style.product__container}>
       <div className={style.search__container}>
      <input
        ref={inputSearch}
        onKeyDown={onSearchSubmit}
        onChange={onChangeTextSearch}
        type="text"
        placeholder=" Buscar producto"
      />
      <button className="btn btn__primary" onClick={onClickSearchSubmit}>buscar</button>
      </div>
      <div className={style.card__container}>
        {productsList?.data?.map((product) => (
          <div className={style.card} key={product.id}>
            <img className={style.image} src={product.image} />
            <div  className={style.card__details}>
            <span className={style.card__name} > {product.name} </span>
            <span className={style.card__description}> <span className={style.card__title}></span> Descripci√≥n: {product.description} </span>
            <span className={style.card__price} > <span className={style.card__title}></span>  ${product.price}</span>
            <span className={style.card__quantity} > <span className={style.card__title}> Disponibles: </span> {product.stock} </span>
          </div>
          </div>
        ))}
      </div>
    </div>
  );
};
